import React, { Fragment, useState, useEffect } from "react";

const UnflagButton = ( { flag } ) => {
    
    const [flags, setFlag] = useState([]);

    const unflag = async e => {
        e.preventDefault();
        try {
            const deleteFlag = await fetch(`http://localhost:5000/flags/${flag.flag_id}`, {
                method: "DELETE"
            });

            setFlag([]);
        } catch (err) {
            console.error(err.message);
        }
    }
    
    return (
        <button 
          type="button" 
          className="btn btn-warning" 
          data-target={`#id${flag.flag_id}`}
          onClick = {e => unflag(e)}
        >
          DELETE FLAG
        </button>
    );
}

export default UnflagButton;
