            const deleteFlag = await fetch(`http://localhost:5000/flags/${flag.flag_id}`, {
                method: "DELETE"
            });

            window.location = "/reports";
        } catch (err) {
            console.error(err.message);
        }
    }

    useEffect(() => {
        getUserInfo();
    }, [isFlag]);
    
    return (
        <Fragment>
            { !isFlag ? 
