import React, { Fragment, useEffect, useState } from "react";

const PostModal = ( { id } ) => {

    //get post

    const getPost = async () => {
        try{
            const response = await fetch(`http://localhost:5000/posts/${id}`, {
                method: "GET",
            });

            window.location = "/";
        }catch(err){
            console.error(err.message);
        }
    }

    return(
      <Fragment>
          <button className="btn btn-outline-primary">
            Post
          </button>
      </Fragment>
    );
};

export default PostModal;
