import React, { Fragment, useState, useEffect } from "react";

const Unflag = ( flag ) => {
    
    const unflag = async e => {
        e.preventDefault();
        try {
            const deleteFlag = await fetch(`http://localhost:5000/flags/${flag.flag_id}`, {
                method: "DELETE"
            });

            window.location = "/reports";
        } catch (err) {
            console.error(err.message);
        }
    }
    return (
        <Fragment>
            <button 
                type="button" 
                className="btn btn-danger" 
                data-target={`#id${flag.flag_id}`}
                onClick = {e => unflag(e)}
            >
                Delete Flag
            </button>
        </ Fragment>
    );
}

export default Unflag;
